import {
  View,
  Text,
  Modal,
  TouchableOpacity,
  StyleSheet,
  Image,
} from "react-native";
import React from "react";
import { useModal } from "../utils/Context";
import Icon from "react-native-vector-icons/FontAwesome";
import {
  doc,
  getDoc,
  setDoc,
} from "firebase/firestore";
import { db } from "../firebase/config";

export default function CustomModal() {
  const { modalVisible, setModalVisible, selectedItem, userData } = useModal();
  const updateItem = async () => {
    const docRef = doc(db, "items", selectedItem.id);
    const docSnap = await getDoc(docRef);
    if (docSnap.exists()) {
        const data = docSnap.data();
        const newdata = {
          ...data,
          status: "borrowed",
          rentedto: userData.username,
          available: false,
          timestamp: new Date().getTime(),
        };
        await setDoc(docRef, newdata);
        alert("Borrow Request Sent");
    } else {
        alert("No such document!");
    }
    };
  return (
    <View>
      <Modal
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          setModalVisible(!modalVisible);
        }}
      >
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <View>
              <Image
                style={styles.imageview}
                source={require("../assets/electronics.jpg")}
              />
            </View>

            <View
              style={{ display: "flex", flexDirection: "row", width: "100%" }}
            >
              <View
                style={{
                  display: "flex",
                  flexDirection: "column",
                  width: "70%",
                }}
              >
                <Text style={styles.modalText}>
                  Item: {selectedItem.itemname}
                </Text>
                <Text style={styles.modalText}>
                  Owner: {selectedItem.owner}
                </Text>
              </View>
              <View
                style={{
                  display: "flex",
                  flexDirection: "column",
                  width: "30%",
                }}
              >
                <Text style={styles.modalTextPrice}>
                  <Icon name="dollar" size={20} color="green" />
                  {selectedItem.price}
                </Text>
              </View>
            </View>
            <Text style={styles.modalText}>Description:</Text>
            <Text style={styles.Description}>{selectedItem.description}</Text>
            <View
              style={{
                display: "flex",
                flexDirection: "row",
                width: "100%",
                justifyContent: "space-evenly",
              }}
            >
              <TouchableOpacity
                style={styles.button2}
                onPress={() => updateItem()}
              >
                <Text style={styles.textStyle}>Borrow Request</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.button1}
                onPress={() => setModalVisible(!modalVisible)}
              >
                <Text style={styles.textStyle}>Close</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
}
const styles = StyleSheet.create({
  imageview: {
    height: 200,
  },
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  modalView: {
    borderColor: "black",
    borderWidth: 2,
    backgroundColor: "white",
    borderRadius: 20,
    paddingHorizontal: 10,
    paddingVertical: 20,
    width: 300,
    alignItems: "center",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },
  button1: {
    borderRadius: 10,
    elevation: 2,
    backgroundColor: "#2196F3",
    width: 100,
    height: 50,
    justifyContent: "center",
  },
  button2: {
    borderRadius: 10,
    height: 50,
    elevation: 2,
    backgroundColor: "#f87171",
    width: 100,
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center",
  },
  modalText: {
    marginBottom: 10,
    textAlign: "center",
    alignSelf: "flex-start",
    fontSize: 15,
    fontWeight: "bold",
  },
  modalTextPrice: {
    marginBottom: 15,
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 20,
    alignSelf: "flex-end",
    color: "green",
  },
  Description: {
    marginBottom: 15,
    textAlign: "center",
    fontSize: 15,
  },
});
