import { View, Text, StyleSheet, Image } from "react-native";
import React from "react";
import MaterialIcons from "react-native-vector-icons/MaterialIcons";

export default function CategoryComponent({
  itemname,
  price,
  description,
  owner,
  id,
  timestamp
}) {
  return (
    <View style={styles.container}>
      <Image
        style={styles.image}
        source={require("../assets/laptopicon.png")}
      />
      <View style={styles.circle}>
        <MaterialIcons name="favorite-border" size={24} color="black" />
      </View>
      <View style={styles.TextView}>
        <Text style={styles.category}>Item: {itemname}</Text>
        <Text style={styles.owner}>Owner:{owner}</Text>
        <Text style={styles.price}>Rs {price} / hour</Text>
        {timestamp ? <Text style={styles.description}>Rented Date {timestamp}</Text> : null}
      </View>
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    backgroundColor: "#e0f2fe",
    paddingVertical: 20,
    marginVertical: 20,
    borderRadius: 10,
    width: "100%",
    display: "flex",
    flexDirection: "row",
    justifyContent: "flex-end",
    height: 120,
  },
  image: {
    width: 100,
    height: 100,
    borderRadius: 10,
    position: "absolute",
    left: 20,
  },
  TextView: {
    width: "60%",
    height: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  category: {
    fontSize: 15,
    color: "gray",
  },
  price: {
    fontSize: 20,
    fontWeight: "bold",
    color: "gray",
  },
  description: {
    fontSize: 15,
    color: "gray",
  },
  owner: {
    fontSize: 15,
    color: "gray",
  },
  circle: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: "white",
    position: "absolute",
    right: 20,
    top: 20,
    alignItems: "center",
    justifyContent: "center",
  },
});
